# Authorization
AUTH_URL = 'https://accounts.spotify.com/api/token'
REDIRECT_URI = 'https://discordapp.com/'

# Web API
BASE_URL = 'https://api.spotify.com/'
SEARCH_URL = BASE_URL + 'v1/search/?q={}&type={}&limit={}'
TRACK_URL = BASE_URL + 'v1/tracks/{}'
ALBUM_URL = BASE_URL + 'v1/albums/{}'
ARTIST_URL = BASE_URL + 'v1/artists/{}'
PLAYLIST_URL = BASE_URL + 'v1/users/{}/playlists/{}'
