GENRE_PAT = re.compile(r'\(([0-9]+)\)')

# dict containing format specifiers to be used to display preview
FORMAT = {
    '%y': 'date',
    '%l': 'album',
    '%t': 'title',
    '%n': 'track',
    '%a': 'artist',
    '%c': 'comment',
    # '%g': 'genre',
    '%A': 'album_artist'
}

# for matching format specifiers
FORMAT_PAT = re.compile(r'%.')

# dict with  {name of textbox: name of field like artist. album, etc}
TAG_FIELDS = {
    'tit': 'title',
    'alb': 'album',
    'gen': 'genre',
    'tno': 'track',
    'art': 'artist',
    'com': 'comment',
    'ala': 'album_artist'
}

# value of date must match this regex
DATE_PATTERN = re.compile(r"""(?x)\s*
                ((?P<year>[0-9]{4})       # YYYY
                (-(?P<month>[01][0-9])    # -MM
                (-(?P<day>[0-3][0-9])     # -DD
                )?)?)?
                [ T]?
                ((?P<hour>[0-2][0-9])     #  HH
                (:(?P<min>[0-6][0-9])     # :MM
                (:(?P<sec>[0-6][0-9])     # :SS
                )?)?)?\s*
                """)

# name of keypress handler which require a update of status line
